mapper.delete:
update IMOS_TA_Cylinder
set deleteFlag = 1, updateTime = GETDATE()
where Cylinder_IDNum = #{CylinderIDNum}

mapper.update:
		update IMOS_TA_Cylinder
		set
		Cylinder_IDNum = #{cylinderIDNum, jdbcType=VARCHAR}
        ,Cylinder_Number = #{cylinderNumber, jdbcType=VARCHAR}
        ,Cylinder_Code = #{cylinderCode, jdbcType=VARCHAR}
        ,Cylinder_QRCode = #{cylinderQRCode, jdbcType=VARCHAR}
        ,Company_Name = #{companyName, jdbcType=VARCHAR}
        ,Cylinder_Spec = #{cylinderSpec, jdbcType=VARCHAR}
        ,Cylinder_Pressure = #{cylinderPressure, jdbcType=VARCHAR}
        ,Cylinder_Volume = #{cylinderVolume, jdbcType=VARCHAR}
        ,Cylinder_MakeDate = #{cylinderMakeDate, jdbcType=VARCHAR}
        ,Cylinder_Type = #{cylinderType, jdbcType=VARCHAR}
        ,Cylinder_Media = #{cylinderMedia, jdbcType=VARCHAR}
        ,Cylinder_Status = #{cylinderStatus, jdbcType=VARCHAR}
        ,Cylinder_ScrapDate = #{cylinderScrapDate, jdbcType=VARCHAR}
        ,Cylinder_Last_InspectDate = #{cylinderLastInspectDate, jdbcType=VARCHAR}
        ,Cylinder_Next_InspectDate = #{cylinderNextInspectDate, jdbcType=VARCHAR}
        ,Cylinder_Clinet = #{cylinderClinet, jdbcType=VARCHAR}
        ,Property1 = #{property1, jdbcType=VARCHAR}
        ,Property2 = #{property2, jdbcType=VARCHAR}
		,Remark = #{remark, jdbcType=VARCHAR}
		,Creation_Date = #{creationDate, jdbcType=TIMESTAMP}
		,Created_By = #{createdBy, jdbcType=INTEGER}
		,Last_Update_Date = #{lastUpdateDate, jdbcType=TIMESTAMP}
		,Last_Updated_By = #{lastUpdatedBy, jdbcType=INTEGER}
		where Cylinder_IDNum = #{CylinderIDNum}

mapper.select:
select

from IMOS_TA_Cylinder


        and Cylinder_IDNum = #{CylinderIDNum, jdbcType=VARCHAR}



        and Cylinder_Number = #{cylinderNumber, jdbcType=VARCHAR}

select

from IMOS_TA_Cylinder


        and Cylinder_QRCode = #{cylinderQRCode, jdbcType=VARCHAR}
