mapper.delete:
update IMOS_ERP_List
set deleteFlag = 1, updateTime = GETDATE()
where List_No = #{单据编号}

mapper.select:
select

from IMOS_ERP_List
where List_No = #{单据编号}

select
count(List_No) c
from IMOS_ERP_List


       List_Type = #{单据类型, jdbcType=VARCHAR}


        and List_Falg = #{单据标识, jdbcType=INTEGER}


        and List_Status = #{单据状态, jdbcType=VARCHAR}


        and List_Date = #{单据日期, jdbcType=TIMESTAMP}


        and Operator_Code = #{操作人编号, jdbcType=VARCHAR}


        and Operator_Name = #{操作人名称, jdbcType=VARCHAR}


        and Material_Code = #{物料编号, jdbcType=VARCHAR}


        and Material_Name = #{物料名称, jdbcType=VARCHAR}


        and Material_Type_Code = #{类型代码, jdbcType=VARCHAR}


        and Material_Type_Name = #{类型名称, jdbcType=VARCHAR}


        and Material_Spec = #{物料规格, jdbcType=VARCHAR}


        and Material_Unit = #{物料单位, jdbcType=VARCHAR}


        and Qty = #{数量, jdbcType=NUMERIC}


        and Plan_Qty = #{计划数量, jdbcType=NUMERIC}


        and Base_Metering_Qty = #{基本计量单位数量, jdbcType=NUMERIC}


        and Base_Metering_Unit = #{基本计量单位, jdbcType=VARCHAR}


        and Metering_Unit = #{计量单位, jdbcType=VARCHAR}


        and Batch_No = #{批次, jdbcType=VARCHAR}


        and Product_Code = #{产品编码, jdbcType=VARCHAR}


        and Object_Type = #{事物类型, jdbcType=VARCHAR}


        and Business_Date = #{业务日期, jdbcType=TIMESTAMP}


        and Plan_Start_Date = #{计划开工日期, jdbcType=TIMESTAMP}


        and Plan_End_Date = #{计划完工日期, jdbcType=TIMESTAMP}


        and Product_Return_Date = #{生产订单回传日期, jdbcType=TIMESTAMP}


        and Store_Code = #{仓库, jdbcType=VARCHAR}


        and Start_Station = #{起始站点, jdbcType=VARCHAR}


        and End_Station = #{目的站点, jdbcType=VARCHAR}


        and Cost_Object = #{成本对象, jdbcType=VARCHAR}


        and Cost_Center = #{成本中心, jdbcType=VARCHAR}

select

from IMOS_ERP_List


        List_No = #{单据编号, jdbcType=VARCHAR}


        and List_Type = #{单据类型, jdbcType=VARCHAR}


        and List_Falg = #{单据标识, jdbcType=INTEGER}


        and List_Status = #{单据状态, jdbcType=VARCHAR}


        and List_Date = #{单据日期, jdbcType=TIMESTAMP}


        and Operator_Code = #{操作人编号, jdbcType=VARCHAR}


        and Operator_Name = #{操作人名称, jdbcType=VARCHAR}


        and Material_Code = #{物料编号, jdbcType=VARCHAR}


        and Material_Name = #{物料名称, jdbcType=VARCHAR}


        and Material_Type_Code = #{类型代码, jdbcType=VARCHAR}


        and Material_Type_Name = #{类型名称, jdbcType=VARCHAR}


        and Material_Spec = #{物料规格, jdbcType=VARCHAR}


        and Material_Unit = #{物料单位, jdbcType=VARCHAR}


        and Qty = #{数量, jdbcType=NUMERIC}


        and Plan_Qty = #{计划数量, jdbcType=NUMERIC}


        and Base_Metering_Qty = #{基本计量单位数量, jdbcType=NUMERIC}


        and Base_Metering_Unit = #{基本计量单位, jdbcType=VARCHAR}


        and Metering_Unit = #{计量单位, jdbcType=VARCHAR}


        and Batch_No = #{批次, jdbcType=VARCHAR}


        and Product_Code = #{产品编码, jdbcType=VARCHAR}


        and Object_Type = #{事物类型, jdbcType=VARCHAR}


        and Business_Date = #{业务日期, jdbcType=TIMESTAMP}


        and Plan_Start_Date = #{计划开工日期, jdbcType=TIMESTAMP}


        and Plan_End_Date = #{计划完工日期, jdbcType=TIMESTAMP}


        and Product_Return_Date = #{生产订单回传日期, jdbcType=TIMESTAMP}


        and Store_Code = #{仓库, jdbcType=VARCHAR}


        and Start_Station = #{起始站点, jdbcType=VARCHAR}


        and End_Station = #{目的站点, jdbcType=VARCHAR}


        and Cost_Object = #{成本对象, jdbcType=VARCHAR}


        and Cost_Center = #{成本中心, jdbcType=VARCHAR}

select

from IMOS_ERP_List
WHERE List_Falg = 0 AND List_No = #{单据编号} AND List_Type = #{单据类型} AND Material_Name = #{物料名称}
