mapper.delete:
update IMOS_TA_Filling
set deleteFlag = 1, updateTime = GETDATE()
where Cylinder_IDNum = #{cylinderIDNum}

mapper.update:
		update IMOS_TA_Filling
		set
		Filling_Date = #{fillingDate, jdbcType=VARCHAR}
        ,Filling_Temperature = #{fillingTemperature, jdbcType=VARCHAR}
        ,Cylinder_IDNum = #{cylinderIDNum, jdbcType=VARCHAR}
        ,Filling_Inspectors = #{fillingInspectors, jdbcType=VARCHAR}
        ,Filling_SignWeight = #{fillingSignWeight, jdbcType=VARCHAR}
        ,Filling_ActualWeight = #{fillingActualWeight, jdbcType=VARCHAR}
        ,Filling_SumWeight = #{fillingSumWeight, jdbcType=VARCHAR}
        ,Filling_FillWeight = #{fillingFillWeight, jdbcType=VARCHAR}
        ,Filling_Fillman = #{fillingFillman, jdbcType=VARCHAR}
        ,Filling_AgainWeight = #{fillingAgainWeight, jdbcType=VARCHAR}
		where Cylinder_IDNum = #{cylinderIDNum}

mapper.select:
select

from IMOS_TA_Filling


        and Cylinder_IDNum = #{cylinderIDNum, jdbcType=VARCHAR}


        and Filling_Date = #{fillingDate, jdbcType=VARCHAR}
