mapper.delete:
update IMOS_WMS_List_Detail
set deleteFlag = 1, updateTime = GETDATE()
where List_No = #{单据编号}

mapper.update:
update IMOS_WMS_List_Detail
set
List_No = #{单据编号, jdbcType=VARCHAR}
,List_DetailNo = #{单据明细编号, jdbcType=VARCHAR}
,Business_No = #{ERP单据编号, jdbcType=VARCHAR}
,Order_No = #{订单编号, jdbcType=VARCHAR}
,Material_Code = #{物料编号, jdbcType=VARCHAR}
,Material_Name = #{物料名称, jdbcType=VARCHAR}
,Material_TypeCode = #{类型代码, jdbcType=VARCHAR}
,Material_TypeName = #{类型名称, jdbcType=VARCHAR}
,Material_Spec = #{物料规格, jdbcType=VARCHAR}
,Material_Unit = #{物料单位, jdbcType=VARCHAR}
,Qty = #{数量, jdbcType=NUMERIC}
where List_No = #{单据编号}

mapper.select:
select

from IMOS_WMS_List_Detail
where List_No = #{单据编号}

select
count(List_No) c
from IMOS_WMS_List_Detail


        and List_DetailNo = #{单据明细编号, jdbcType=VARCHAR}


        and Business_No = #{ERP单据编号, jdbcType=VARCHAR}



        and Order_No = #{订单编号, jdbcType=VARCHAR}



        and Material_Code = #{物料编号, jdbcType=VARCHAR}



        and Material_Name = #{物料名称, jdbcType=VARCHAR}



        and Material_TypeCode = #{类型代码, jdbcType=VARCHAR}



        and  Material_TypeName = #{类型名称, jdbcType=VARCHAR}



        and Material_Spec = #{物料规格, jdbcType=VARCHAR}



        and Material_Unit = #{物料单位, jdbcType=VARCHAR}



        and Qty = #{数量, jdbcType=NUMERIC}

select

from IMOS_WMS_List_Detail


        and List_DetailNo = #{单据明细编号, jdbcType=VARCHAR}


        and Business_No = #{ERP单据编号, jdbcType=VARCHAR}


        and Order_No = #{订单编号, jdbcType=VARCHAR}


        and Material_Code = #{物料编号, jdbcType=VARCHAR}


        and Material_Name = #{物料名称, jdbcType=VARCHAR}


        and Material_TypeCode = #{类型代码, jdbcType=VARCHAR}


        and  Material_TypeName = #{类型名称, jdbcType=VARCHAR}


        and Material_Spec = #{物料规格, jdbcType=VARCHAR}


        and Material_Unit = #{物料单位, jdbcType=VARCHAR}


        and Qty = #{数量, jdbcType=NUMERIC}

select

from IMOS_WMS_List_Detail

    List_No = #{单据编号}
    OR List_DetailNo = #{单据明细编号}
    OR Business_No = #{ERP单据编号}
    OR Order_No = #{订单编号}
    OR Material_Code = #{物料编号}
    OR Material_Name = #{物料名称}
    OR Material_TypeCode = #{类型代码}
    OR Material_TypeName = #{类型名称}
    OR Material_Spec = #{物料规格}
    OR Material_Unit = #{物料单位}
    OR Qty = #{数量}
