mapper.delete:
update IMOS_LO_Bin
set deleteFlag = 1, upTIMESTAMP = GETDATE()
where Material_Code = #{物料代码}

mapper.update:
update IMOS_LO_Bin
set
Material_Code = #{物料代码, jdbcType=VARCHAR}
,Material_Name = #{物料名称, jdbcType=VARCHAR}
,Bar_Code = #{条码编号, jdbcType=VARCHAR}
,Material_TypeCode = #{类型代码, jdbcType=VARCHAR}
,Material_TypeName = #{类型名称, jdbcType=VARCHAR}
,Pallet_No = #{托盘编号, jdbcType=VARCHAR}
,Bin_No = #{库位编号, jdbcType=VARCHAR}
,Bin_Type = #{库位类型, jdbcType=VARCHAR}
,Bin_Status = #{库位状态, jdbcType=VARCHAR}
,Bin_UseFlag = #{库位禁用, jdbcType=INTEGER}
,Material_Spec = #{物料规格, jdbcType=VARCHAR}
,Plan_No = #{计划编号, jdbcType=VARCHAR}
,Batch_No = #{生产批次, jdbcType=VARCHAR}
,Material_Weight = #{物料重量, jdbcType=NUMERIC}
,Product_Date = #{生产日期, jdbcType=TIMESTAMP}
,In_Date = #{入库时间, jdbcType=TIMESTAMP}
where Material_Code = #{物料代码}

mapper.select:
select

from IMOS_LO_Bin
where Material_Code = #{物料代码}

select
count(Material_Code) c
from IMOS_LO_Bin


        and Bar_Code = #{条码编号, jdbcType=VARCHAR}


        and Material_Name = #{物料名称, jdbcType=VARCHAR}


        and Material_TypeCode = #{类型代码, jdbcType=VARCHAR}



        and Material_TypeName = #{类型名称, jdbcType=VARCHAR}



        and Pallet_No = #{托盘编号, jdbcType=VARCHAR}



        and Bin_No = #{库位编号, jdbcType=VARCHAR}



        and Bin_Type = #{库位类型, jdbcType=VARCHAR}



        and Bin_Status = #{库位状态, jdbcType=VARCHAR}



        and Bin_UseFlag = #{库位禁用, jdbcType=INTEGER}



        and Material_Spec = #{物料规格, jdbcType=VARCHAR}



        and Plan_No = #{计划编号, jdbcType=VARCHAR}



        and Batch_No = #{生产批次, jdbcType=VARCHAR}



        and Material_Weight = #{物料重量, jdbcType=NUMERIC}



        and Product_Date = #{生产日期, jdbcType=TIMESTAMP}



        and In_Date = #{入库时间, jdbcType=TIMESTAMP}

select

from IMOS_LO_Bin


        and Material_Code = #{物料代码, jdbcType=VARCHAR}


        and Material_Name = #{物料名称, jdbcType=VARCHAR}


        and Bar_Code = #{条码编号, jdbcType=VARCHAR}


        and Material_TypeCode = #{类型代码, jdbcType=VARCHAR}


        and Material_TypeName = #{类型名称, jdbcType=VARCHAR}


        and Pallet_No = #{托盘编号, jdbcType=VARCHAR}


        and Bin_No = #{库位编号, jdbcType=VARCHAR}


        and Bin_Type = #{库位类型, jdbcType=VARCHAR}


        and Bin_Status = #{库位状态, jdbcType=VARCHAR}


        and Bin_UseFlag = #{库位禁用, jdbcType=INTEGER}


        and Material_Spec = #{物料规格, jdbcType=VARCHAR}


        and Plan_No = #{计划编号, jdbcType=VARCHAR}


        and Batch_No = #{生产批次, jdbcType=VARCHAR}


        and Material_Weight = #{物料重量, jdbcType=NUMERIC}


        and Product_Date = #{生产日期, jdbcType=TIMESTAMP}


        and In_Date = #{入库时间, jdbcType=TIMESTAMP}

select

from IMOS_LO_Bin

    Material_Code = #{物料代码}
    OR Material_Name = #{物料名称}
    OR Bar_Code = #{条码编号}
    OR Material_TypeCode = #{类型代码}
    OR Material_TypeName = #{类型名称}
    OR Pallet_No = #{托盘编号}
    OR Bin_No = #{库位编号}
    OR Bin_Type = #{库位类型}
    OR Bin_Status = #{库位状态}
    OR Bin_UseFlag = #{库位禁用}
    OR Material_Spec = #{物料规格}
    OR Plan_No = #{计划编号}
    OR Batch_No = #{生产批次}
    OR Material_Weight = #{物料重量}
    OR Product_Date = #{生产日期}
    OR In_Date = #{入库时间}

select

from IMOS_LO_Bin
where Bar_Code = #{条码编号}

mapper.update:
update IMOS_LO_Bin
set
Bin_Status = #{库位状态}
where Bin_No = #{库位编号}

mapper.select:
select

from  IMOS_LO_Bin
WHERE Bin_Status = '$' AND Material_Name = #{物料名称} AND Batch_No = #{生产批次} AND Material_Spec = #{物料规格}

select

from  IMOS_LO_Bin
WHERE Bin_Status = '$' AND Bin_Type = 'E' AND Material_Name = #{物料名称} AND Batch_No = #{生产批次} AND Material_Spec = #{物料规格}

select

from  IMOS_LO_Bin
WHERE Bin_Status = '$' AND Bin_Type = 'O' AND Material_Name = #{物料名称} AND Batch_No = #{生产批次} AND Material_Spec = #{物料规格}

