mapper.delete:
update IMOS_LO_Task_History
set deleteFlag = 1, upTIMESTAMP = GETDATE()
where  Task_No = #{任务编号}

mapper.update:
update IMOS_LO_Task_History
set
 Task_No = #{任务编号, jdbcType=VARCHAR}
,List_No = #{单据编号, jdbcType=VARCHAR}
,AGV_Code = #{AGV编号, jdbcType=VARCHAR}
,Task_Type = #{任务类型, jdbcType=VARCHAR}
,Task_Status = #{任务状态, jdbcType=VARCHAR}
,Task_Priority = #{任务优先级, jdbcType=VARCHAR}
,List_DetailNo = #{单据明细编号, jdbcType=VARCHAR}
,Material_Code = #{物料编号, jdbcType=VARCHAR}
,Material_Name = #{物料名称, jdbcType=VARCHAR}
,Material_Type_Code = #{类型代码, jdbcType=VARCHAR}
,Material_Type_Name = #{类型名称, jdbcType=VARCHAR}
,Material_Spec = #{物料规格, jdbcType=VARCHAR}
,Start_Station = #{起始站点, jdbcType=VARCHAR}
,End_Station = #{目的站点, jdbcType=VARCHAR}
,Batch_No = #{批次, jdbcType=VARCHAR}
,Qty = #{数量, jdbcType=NUMERIC}
,Remark = #{备注, jdbcType=VARCHAR}
,Start_Time = #{开始时间, jdbcType=TIMESTAMP}
,End_Time = #{结束时间, jdbcType=TIMESTAMP}
where  Task_No = #{任务编号}

mapper.select:
select

from IMOS_LO_Task_History
where  Task_No = #{任务编号}

select
count( Task_No) c
from IMOS_LO_Task_History


        and AGV_Code = #{AGV编号, jdbcType=VARCHAR}


        and List_No = #{单据编号, jdbcType=VARCHAR}


        and Task_Type = #{任务类型, jdbcType=VARCHAR}



        and Task_Status = #{任务状态, jdbcType=VARCHAR}



        and Task_Priority = #{任务优先级, jdbcType=VARCHAR}



        and List_DetailNo = #{单据明细编号, jdbcType=VARCHAR}



        and Material_Code = #{物料编号, jdbcType=VARCHAR}



        and Material_Name = #{物料名称, jdbcType=VARCHAR}



        and Material_Type_Code = #{类型代码, jdbcType=VARCHAR}



        and Material_Type_Name = #{类型名称, jdbcType=VARCHAR}



        and Material_Spec = #{物料规格, jdbcType=VARCHAR}



        and Start_Station = #{起始站点, jdbcType=VARCHAR}



        and End_Station = #{目的站点, jdbcType=VARCHAR}


        and Batch_No = #{批次, jdbcType=VARCHAR}


        and Qty = #{数量, jdbcType=NUMERIC}



        and Remark = #{备注, jdbcType=VARCHAR}



        and Start_Time = #{开始时间, jdbcType=TIMESTAMP}



        and End_Time = #{结束时间, jdbcType=TIMESTAMP}

