mapper.delete:
update IMOS_PD_CheckPlan
set deleteFlag = 1, updateTime = GETDATE()
where CheckPlan_Code = #{计划编号}

mapper.update:
update IMOS_PD_CheckPlan
set
CheckPlan_Code = #{计划编号, jdbcType=VARCHAR}
,CheckPlan_Name = #{盘点名称, jdbcType=VARCHAR}
,CheckPlan_Way = #{盘点方式, jdbcType=VARCHAR}
,CheckPlan_Project = #{盘点项目, jdbcType=VARCHAR}
,CheckPlan_OperatorCode = #{盘点人编号, jdbcType=VARCHAR}
,CheckPlan_OperatorName = #{盘点人名称, jdbcType=VARCHAR}
,CheckPlan_StartDate = #{盘点开始时间, jdbcType=TIMESTAMP}
,CheckPlan_EndDate = #{盘点结束时间, jdbcType=TIMESTAMP}
,CheckPlan_Result = #{完成程度, jdbcType=VARCHAR}
where CheckPlan_Code = #{计划编号}

mapper.select:
select

from IMOS_PD_CheckPlan
where CheckPlan_Code = #{计划编号}

select
count(CheckPlan_Code) c
from IMOS_PD_CheckPlan


        and CheckPlan_Name = #{盘点名称, jdbcType=VARCHAR}


        and CheckPlan_Way = #{盘点方式, jdbcType=VARCHAR}



        and CheckPlan_Project = #{盘点项目, jdbcType=VARCHAR}



        and CheckPlan_OperatorCode = #{盘点人编号, jdbcType=VARCHAR}



        and CheckPlan_OperatorName = #{盘点人名称, jdbcType=VARCHAR}



        and CheckPlan_StartDate = #{盘点开始时间, jdbcType=TIMESTAMP}



        and CheckPlan_EndDate = #{盘点结束时间, jdbcType=TIMESTAMP}



        and CheckPlan_Result = #{完成程度, jdbcType=VARCHAR}

select

from IMOS_PD_CheckPlan


        and CheckPlan_Code = #{计划编号, jdbcType=VARCHAR}


        and CheckPlan_Name = #{盘点名称, jdbcType=VARCHAR}


        and CheckPlan_Way = #{盘点方式, jdbcType=VARCHAR}


        and CheckPlan_Project = #{盘点项目, jdbcType=VARCHAR}


        and CheckPlan_OperatorCode = #{盘点人编号, jdbcType=VARCHAR}


        and CheckPlan_OperatorName = #{盘点人名称, jdbcType=VARCHAR}


        and CheckPlan_StartDate = #{盘点开始时间, jdbcType=TIMESTAMP}


        and CheckPlan_EndDate = #{盘点结束时间, jdbcType=TIMESTAMP}


        and CheckPlan_Result = #{完成程度, jdbcType=VARCHAR}

select

from IMOS_PD_CheckPlan

    CheckPlan_Code = #{计划编号}
    OR CheckPlan_Name = #{盘点名称}
    OR CheckPlan_Way = #{盘点方式}
    OR CheckPlan_Project = #{盘点项目}
    OR CheckPlan_OperatorCode = #{盘点人编号}
    OR CheckPlan_OperatorName = #{盘点人名称}
    OR CheckPlan_StartDate = #{盘点开始时间}
    OR CheckPlan_EndDate = #{盘点结束时间}
    OR CheckPlan_Result = #{完成程度}
