mapper.update:
update IMOS_VS_TerminalVersion
set
Version_Code = #{版本号, jdbcType=INTEGER}
,Version_Display_Code = #{版本显示号, jdbcType=VARCHAR}
,Version_Description = #{版本描述, jdbcType=VARCHAR}
,Download_Address = #{下载地址, jdbcType=VARCHAR}
,Creat_Time = #{创建时间, jdbcType=TIMESTAMP}
where VersionID = #{版本id}

mapper.select:
select

from IMOS_VS_TerminalVersion
where VersionID = #{版本id}

select
count(VersionID) c
from IMOS_VS_TerminalVersion


		and Version_Code = #{版本号, jdbcType=INTEGER}



		and Version_Display_Code = #{版本显示号, jdbcType=VARCHAR}



		and Version_Description = #{版本描述, jdbcType=VARCHAR}



		and Download_Address = #{下载地址, jdbcType=VARCHAR}



		and Creat_Time = #{创建时间, jdbcType=TIMESTAMP}


		and Creat_Time >= #{创建时间Begin, jdbcType=TIMESTAMP}


		and Creat_Time <= #{创建时间End, jdbcType=TIMESTAMP}

select *
from
(select
row_number() over (order by VersionID) as rowindex,

from IMOS_VS_TerminalVersion


		and Version_Code = #{版本号, jdbcType=INTEGER}


		and Version_Display_Code = #{版本显示号, jdbcType=VARCHAR}


		and Version_Description = #{版本描述, jdbcType=VARCHAR}


		and Download_Address = #{下载地址, jdbcType=VARCHAR}


		and Creat_Time = #{创建时间, jdbcType=TIMESTAMP}


		and Creat_Time >= #{创建时间Begin, jdbcType=TIMESTAMP}


		and Creat_Time <= #{创建时间End, jdbcType=TIMESTAMP}

) as t
where rowindex  >= #{startIndex} and rowindex < #{endIndex}

select

from IMOS_VS_TerminalVersion


		and Version_Code = #{版本号, jdbcType=INTEGER}


		and Version_Display_Code = #{版本显示号, jdbcType=VARCHAR}


		and Version_Description = #{版本描述, jdbcType=VARCHAR}


		and Download_Address = #{下载地址, jdbcType=VARCHAR}


		and Creat_Time = #{创建时间, jdbcType=TIMESTAMP}


		and Creat_Time >= #{创建时间Begin, jdbcType=TIMESTAMP}


		and Creat_Time <= #{创建时间End, jdbcType=TIMESTAMP}
